$(function () {
    var e = $("#pull");
    var isOpen = false;
    menu = $("nav ul"), menuHeight = menu.height(), $(e).on("click", function (e) {
        if(!isOpen) {
            $('.site-header').css('height', 'auto');
            isOpen = true;
        } else {
            isOpen = false;            
        }
        menu.slideToggle({
            complete: function(){
                if(!isOpen){
                    $('.site-header').css('height', '100px');
                }
            }
        });
    })
}), $(window).resize(function () {
    $(window).width() > 320 && menu.is(":hidden") && menu.removeAttr("style")
});
